<template>
  <mt-header title="快捷帮助">
    <mt-button v-link="'/'" icon="back" slot="left"></mt-button>
  </mt-header>
  <div class="header">
    <img src="data:image/png;base64," />
  </div>
  <div class="content">
    <p class="title">{{ title }}</p>
    <p class="time">{{ time }}</p>
    <p class="answer">{{ content }}</p>
  </div>
</template>

<script>
import Bus from '../bus.js'
import { Header, Button, Indicator } from 'mint-ui';
import { showMsg } from '../func/showMsg'

export default {
  data () {
    return {
      // note: changing this line won't causes changes
      // with hot-reload because the reloaded component
      // preserves its current state and we are modifying
      // its initial state.
      title : '',
      time : '',
      content : '',
      mid: window.myconfig.mid,
      qid: 1,
    }
  },
  components : {
    mtHeader : Header,
    mtButton : Button,
  },
  route : {
    // 每次进入该页面，刷新当前问题内容
    activate : function(transition){
      this.mid = this.$route.query.mid;
      this.qid = this.$route.query.qid;

      console.log('router_query_qid: ' +this.$route.query.qid);

      Indicator.open('加载中...');

      var url = window.myconfig.apiRoot + '&do=detailQues&mid='+this.mid+'&qid='+this.qid;
      this.$http.get(url).then((response) => {
          console.log(response.json());
          // success callback
          var resp = response.json();
          if(resp.status !== undefined && resp.status >= 0){
              Bus.$emit('QuesGet', resp.data);
          }

          Indicator.close();
      }, (response) => {
          showMsg('网络异常');
          Indicator.close();
      });

      transition.next();
    }
  },
  ready () {

    // 监听问题详情信息变化
    Bus.$on('QuesGet', data => {
      console.log(data);
      this.title = data.title;
      this.content = data.content;
      this.time = data.time;
    }); 
  }
}
</script>

